components.calculateValue:
let specialCaseFormatter = (name) => {
    if (name == 'BreastDuctal') return 'Breast Ductal';
    if (name == 'BreastNonDuctal') return 'Breast Non-Ductal';
    if (name == 'IntrahepaticBileDuct') return 'Intrahepatic Bile Duct';
    if (name == 'LiverCancerNoHepatitisBNoHepatitisC') return 'Liver Cancer (No Hep BC)';
    return name;
};
value = data.zantacCancerDiagnosis && data.zantacCancerDiagnosis.length > 0 ? data.zantacCancerDiagnosis.map(r => {return specialCaseFormatter(_.capitalize(r.split('Cancer')[0])) + ' Cancer';}).join(',') : '';
